import React, {Component} from 'react';
import {ColoredEdge} from "./ColoredEdge";
import {buildQueries} from "@testing-library/react";

interface EdgeListProps {
    onChange(edges: ColoredEdge[]): void;  // called when a new edge list is ready
                                    // TODO: once you decide how you want to communicate the edges to the App, you should
                                    // change the type of edges so it isn't `any`
}

interface EdgeListState {
    start: string,
    end: string
    buildings: {};
}

/**
 * A text field that allows the user to enter the list of edges.
 * Also contains the buttons that the user will use to interact with the app.
 */
class EdgeList extends Component<EdgeListProps, EdgeListState> {
    constructor(props: any) {
        super(props);
        this.state = {
            start: '',
            end: '',
            buildings: {}
        };
        this.initializeBuildings()
        console.log(this.state);

    }

    initializeBuildings = () => {
        this.initialize().then((res) => {
            this.setState({
                buildings: res
            })
        });
    }

    render() {
        return (
            <div id="edge-list">
                <select onChange={this.startBuildingSelected}>
                    {}
                </select>
                <select onChange={this.endBuildingSelected}>
                    <option></option>
                    <option></option>
                    <option></option>
                    <option></option>
                    <option></option>
                </select>
                {/*Edges <br/>*/}
                {/*<textarea*/}
                {/*    rows={5}*/}
                {/*    cols={30}*/}
                {/*    onChange={(event) => {console.log('textarea onChange was called');*/}
                {/*    this.setState({inputValue: event.target.value})}}*/}
                {/*    value={this.state.inputValue}*/}
                {/*/> <br/>*/}
                <button onClick={this.onDraw}>Draw</button>
                <button onClick={this.onClear}>Clear</button>
            </div>
        );
    }

    initialize = async () => {
        return (await fetch("http://localhost:4567/building")).json();
    }
    /** update the string input to the edges*/
    onDraw = () => {
        console.log(this.state);
    }

    /** clear the input and alert other component*/
    onClear = () => {
        this.setState({start: '', end: ''});
        this.props.onChange([]);
    }

    startBuildingSelected = (e: any) => {
        // set state of b1 to the selected option
        this.setState({start: e.target.value})
    }

    endBuildingSelected = (e: any) => {
        this.setState({end: e.target.value})
    }
}

export default EdgeList;
